mapper.select:
select id, load_type loadType
from t_import_rule
where id = #{id}

select sqlldr_info_id sqlldrInfoId
from t_import_rule
where rule_id = #{ruleId}

mapper.insert:
insert into t_import_rule (id, rule_id, data_source_id, 
  sqlldr_info_id, rule_name, seperator, 
  target_database, source_database, load_type, 
  update_time, sqlldr_info_name, rule_table_id
  )
values (#{id,jdbcType=BIGINT}, #{ruleId,jdbcType=VARCHAR}, #{dataSourceId,jdbcType=VARCHAR}, 
  #{sqlldrInfoId,jdbcType=VARCHAR}, #{ruleName,jdbcType=VARCHAR}, #{seperator,jdbcType=VARCHAR}, 
  #{targetDatabase,jdbcType=VARCHAR}, #{sourceDatabase,jdbcType=VARCHAR}, #{loadType,jdbcType=CHAR}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{sqlldrInfoName,jdbcType=VARCHAR}, #{ruleTableId,jdbcType=VARCHAR}
  )
