mapper.select:
select id, sqlldr_info_name sqlldrInfoName, tns_url tnsUrl, sqlldr_url sqlldrUrl from t_import_sqlldr_info
and sqlldr_info_name like concat('%', #{sqlldrInfoName}, '%')
order by id asc limit #{pageNo}, #{pageSize}

mapper.insert:
insert into t_import_sqlldr_info (id, sqlldr_info_id, sqlldr_info_name, 
  tns_url, update_time, sqlldr_url
  )
values (#{id,jdbcType=BIGINT}, #{sqlldrInfoId,jdbcType=VARCHAR}, #{sqlldrInfoName,jdbcType=VARCHAR}, 
  #{tnsUrl,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, #{sqlldrUrl,jdbcType=VARCHAR}
  )
